// src/components/CharacterCard.js
import React from 'react';
import { Box, Image, Text, Button } from '@chakra-ui/react';

const CharacterCard = ({ character, onFavorite }) => {
  return (
    <Box borderWidth="1px" borderRadius="lg" overflow="hidden" p="6" m="4">
      <Image
        src={`https://starwars-visualguide.com/assets/img/characters/${character.url.split('/')[5]}.jpg`}
        alt={character.name}
        mb="4"
      />
      <Text fontWeight="bold" fontSize="xl">{character.name}</Text>
      <Button mt="4" colorScheme="blue" onClick={() => onFavorite(character)}>
        Add to Favorites
      </Button>
    </Box>
  );
};

export default CharacterCard;
