import React from 'react';
import { Box, Image, Text, Button, VStack, Link } from '@chakra-ui/react';
import { Link as RouterLink } from 'react-router-dom';

const CharacterCard = ({ character, onFavorite }) => {
  const characterId = character.url.split('/')[5];

  return (
    <Box borderWidth="1px" borderRadius="lg" overflow="hidden" p="4" bg="white" boxShadow="md" width="200px" height="350px">
      <VStack>
        <Image
          src={`https://starwars-visualguide.com/assets/img/characters/${characterId}.jpg`}
          alt={character.name}
          mb="4"
          borderRadius="md"
          height="150px"
          objectFit="cover"
        />
        <Text fontWeight="bold" fontSize="md" textAlign="center">{character.name}</Text>
        <Button 
          mt="4" 
          colorScheme="teal" 
          size="sm" 
          onClick={() => onFavorite(character)}
        >
          Add to Favorites
        </Button>
        <Button 
          mt="4" 
          colorScheme="blue" 
          size="sm" 
          as={RouterLink} 
          to={`/character/${characterId}`}
        >
          More Info
        </Button>
      </VStack>
    </Box>
  );
};

export default CharacterCard;
