import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Image, Text, List, ListItem, Button } from '@chakra-ui/react';

const CharacterDetail = () => {
  const { id } = useParams();
  const [character, setCharacter] = useState(null);
  const [films, setFilms] = useState([]);

  useEffect(() => {
    const fetchCharacter = async () => {
      const response = await axios.get(`https://swapi.dev/api/people/${id}/`);
      setCharacter(response.data);

      const filmRequests = response.data.films.map(url => axios.get(url));
      const filmResponses = await Promise.all(filmRequests);
      setFilms(filmResponses.map(res => res.data.title));
    };
    fetchCharacter();
  }, [id]);

  if (!character) return <Text>Loading...</Text>;

  return (
    <Box p="6">
      <Image
        src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`}
        alt={character.name}
        mb="4"
      />
      <Text fontWeight="bold" fontSize="2xl">{character.name}</Text>
      <Text mt="2">Height: {character.height} cm</Text>
      <Text mt="2">Mass: {character.mass} kg</Text>
      <Text mt="2">Hair Color: {character.hair_color}</Text>
      <Text mt="2">Skin Color: {character.skin_color}</Text>
      <Text mt="2">Eye Color: {character.eye_color}</Text>
      <Text mt="2">Birth Year: {character.birth_year}</Text>
      <Text mt="2">Gender: {character.gender}</Text>
      <Text mt="4" fontWeight="bold" fontSize="xl">Films:</Text>
      <List spacing={2}>
        {films.map(film => (
          <ListItem key={film}>{film}</ListItem>
        ))}
      </List>
      <Button 
        mt="4" 
        colorScheme="teal" 
        size="sm" 
        as="a" 
        href="/"
      >
        Back to Characters
      </Button>
    </Box>
  );
};

export default CharacterDetail;
