import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Image, Text, Grid, GridItem, Flex, Heading, Button, VStack, Link } from '@chakra-ui/react';

const CharacterDetail = () => {
  const { id } = useParams();
  const [character, setCharacter] = useState(null);
  const [films, setFilms] = useState([]);

  useEffect(() => {
    const fetchCharacter = async () => {
      const response = await axios.get(`https://swapi.dev/api/people/${id}/`);
      setCharacter(response.data);

      const filmRequests = response.data.films.map(url => axios.get(url));
      const filmResponses = await Promise.all(filmRequests);
      setFilms(filmResponses.map(res => res.data.title));
    };
    fetchCharacter();
  }, [id]);

  if (!character) return <Text>Loading...</Text>;

  return (
    <Box p="6" bg="gray.100" borderRadius="lg" boxShadow="md">
      <Grid templateColumns={{ base: '1fr', md: '1fr 1fr' }} gap="8">
        <Box>
          <Image
            src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`}
            alt={character.name}
            borderRadius="md"
            boxShadow="lg"
            maxH="400px"
            objectFit="cover"
          />
        </Box>
        <Box>
          <VStack align="start" spacing="4">
            <Heading fontSize="3xl">{character.name}</Heading>
            <Text fontWeight="bold">Height:</Text>
            <Text>{character.height} cm</Text>
            <Text fontWeight="bold">Mass:</Text>
            <Text>{character.mass} kg</Text>
            <Text fontWeight="bold">Hair Color:</Text>
            <Text>{character.hair_color}</Text>
            <Text fontWeight="bold">Skin Color:</Text>
            <Text>{character.skin_color}</Text>
            <Text fontWeight="bold">Eye Color:</Text>
            <Text>{character.eye_color}</Text>
            <Text fontWeight="bold">Birth Year:</Text>
            <Text>{character.birth_year}</Text>
            <Text fontWeight="bold">Gender:</Text>
            <Text>{character.gender}</Text>
          </VStack>
        </Box>
      </Grid>

      <Box mt="8">
        <Heading fontSize="xl" mb="4">Films:</Heading>
        <Flex direction="column">
          {films.map(film => (
            <Link key={film} href="#" color="teal.500" mb="2">
              {film}
            </Link>
          ))}
        </Flex>
      </Box>

      <Button 
        mt="8" 
        colorScheme="teal" 
        size="sm" 
        as="a" 
        href="/"
      >
        Back to Characters
      </Button>
    </Box>
  );
};

export default CharacterDetail;
