import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Image, Text, Table, Tbody, Tr, Td, Stack, Heading, Button } from '@chakra-ui/react';

const CharacterDetail = () => {
  const { id } = useParams();
  const [character, setCharacter] = useState(null);
  const [films, setFilms] = useState([]);

  useEffect(() => {
    const fetchCharacter = async () => {
      try {
        const response = await axios.get(`https://swapi.dev/api/people/${id}/`);
        setCharacter(response.data);

        const filmRequests = response.data.films.map(url => axios.get(url));
        const filmResponses = await Promise.all(filmRequests);
        setFilms(filmResponses.map(res => res.data.title));
      } catch (error) {
        console.error('Error fetching character data:', error);
      }
    };
    fetchCharacter();
  }, [id]);

  if (!character) return <Text>Loading...</Text>;

  return (
    <Box p="6" bg="gray.100" borderRadius="lg" boxShadow="md">
      <Stack direction={{ base: 'column', md: 'row' }} spacing="8" align="flex-start">
        <Box>
          <Image
            src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`}
            alt={character.name}
            borderRadius="md"
            boxShadow="lg"
            maxH="400px"
            objectFit="cover"
          />
        </Box>
        <Box flex="1">
          <Stack spacing="4">
            <Heading fontSize="3xl" mb="2">{character.name}</Heading>
            <Table variant="unstyled">
              <Tbody>
                <Tr>
                  <Td fontWeight="bold" width="120px">Height:</Td>
                  <Td>{character.height} cm</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold">Mass:</Td>
                  <Td>{character.mass} kg</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold">Hair Color:</Td>
                  <Td>{character.hair_color}</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold">Skin Color:</Td>
                  <Td>{character.skin_color}</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold">Eye Color:</Td>
                  <Td>{character.eye_color}</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold">Birth Year:</Td>
                  <Td>{character.birth_year}</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold">Gender:</Td>
                  <Td>{character.gender}</Td>
                </Tr>
                <Tr>
                  <Td fontWeight="bold" verticalAlign="top" width="120px">Films:</Td>
                  <Td>
                    <div style={{ borderLeft: '1px solid #ccc', paddingLeft: '10px' }}>
                      <ul style={{ listStyleType: 'none', padding: 0 }}>
                        {films.map((film, index) => (
                          <li key={index} style={{ marginBottom: '4px' }}>{film}</li>
                        ))}
                      </ul>
                    </div>
                  </Td>
                </Tr>
              </Tbody>
            </Table>
          </Stack>
        </Box>
      </Stack>

      <Button 
        mt="6" 
        colorScheme="teal" 
        size="sm" 
        as="a" 
        href="/"
        mx="auto" // Center align the button
        display="block" // Ensures it takes full width
        w="fit-content" // Set width to fit content
      >
        Back to Characters
      </Button>
    </Box>
  );
};

export default CharacterDetail;
