import React from 'react';
import { Box, Image, Text, Button, VStack } from '@chakra-ui/react';
import { Link as RouterLink } from 'react-router-dom';

const CharacterCard = ({ character, onRemove }) => {
  const removeFromFavorites = () => {
    onRemove(character); // Call the onRemove callback with the character to remove
  };

  return (
    <Box borderWidth="1px" borderRadius="lg" overflow="hidden" p="4" bg="white" boxShadow="md" width="200px" height="350px">
      <VStack>
        <Image
          src={`https://starwars-visualguide.com/assets/img/characters/${character.url.split('/')[5]}.jpg`}
          alt={character.name}
          mb="4"
          borderRadius="md"
          height="150px"
          objectFit="cover"
        />
        <Text fontWeight="bold" fontSize="md" textAlign="center">{character.name}</Text>
        {onRemove && ( // Render Remove button only if onRemove callback is provided
          <Button 
            mt="4" 
            colorScheme="red" 
            size="sm" 
            onClick={removeFromFavorites} // Call removeFromFavorites function on click
          >
            Remove from Favorites
          </Button>
        )}
        {!onRemove && ( // Render Add button only if onRemove callback is not provided
          <Button 
            mt="4" 
            colorScheme="teal" 
            size="sm" 
            as={RouterLink} 
            to={`/character/${character.url.split('/')[5]}`}
          >
            More Info
          </Button>
        )}
      </VStack>
    </Box>
  );
};

export default CharacterCard;
